INSERT INTO EXAM (name,description) VALUES('Ecocardiograma bidimensional com doppler','O ecocardiograma bidimensional com Doppler é um exame de ultrassom, no qual as imagens do coração são captadas por um transdutor colocado sobre o tórax do paciente e transmitidas para um monitor');
INSERT INTO EXAM (name,description) VALUES('Raio X contrastado','O Raio X contrastado é um método de estudar órgãos por meio de radiografias e da administração de um corante especial, chamado meio de contraste');
INSERT INTO EXAM (name,description) VALUES('Tomografia computadorizada','A tomografia computadorizada (TC) é um exame não invasivo de diagnóstico que, com imagens geradas via radiação e produzidas por computador, permite a visualização de órgãos, estruturas e tecidos de forma muito mais detalhada que uma radiografia comum');
INSERT INTO EXAM (name,description) VALUES('Ressonância magnética','A ressonância magnética é uma técnica de imagem médica usada em radiologia para formar imagens da anatomia e dos processos fisiológicos do corpo, tanto na saúde como na doença');
INSERT INTO EXAM (name,description) VALUES('Radiografia','Radiografia é uma técnica de exame de imagem que utiliza raios X para ver um material cuja composição não é uniforme como o corpo humano');
